import {
  Component,
  Input,
  Output,
  EventEmitter,
  forwardRef,
  ViewChild,
  ElementRef,
  OnInit,
  HostListener,
  ChangeDetectorRef,
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

export interface SelectOption {
  label: string;
  value: string | number | boolean | null;
}

@Component({
  selector: 'lib-select',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './select.component.html',
  styleUrls: ['./select.component.css'],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SelectComponent),
      multi: true,
    },
  ],
})
export class SelectComponent implements ControlValueAccessor, OnInit {
  @Input() options: SelectOption[] = [];
  @Input() placeholder = 'Select an option';
  @Input() disabled = false;
  @Input() multiple = false;
  @Input() required = false;

  @Output() selectionChange = new EventEmitter<
    string | number | boolean | null | (string | number | boolean | null)[]
  >();
  @Output() openedChange = new EventEmitter<boolean>();

  @ViewChild('searchInput') searchInput!: ElementRef<HTMLInputElement>;
  @ViewChild('dropdown') dropdown!: ElementRef;

  isOpen = false;
  searchText = '';
  filteredOptions: SelectOption[] = [];
  value:
    | string
    | number
    | boolean
    | null
    | (string | number | boolean | null)[] = null;
  touched = false;
  focusedOptionIndex = -1;

  constructor(private cdr: ChangeDetectorRef) {}

  ngOnInit() {
    this.filteredOptions = [...this.options];
  }

  get displayValue(): string {
    if (this.value === null || this.value === undefined) {
      return this.placeholder;
    }

    if (this.multiple) {
      if (Array.isArray(this.value) && this.value !== null) {
        const selectedLabels = this.options
          .filter((opt) =>
            (this.value as (string | number | boolean | null)[]).includes(
              opt.value
            )
          )
          .map((opt) => opt.label);
        return selectedLabels.length > 0
          ? selectedLabels.join(', ')
          : this.placeholder;
      }
      return this.placeholder;
    }

    const selectedOption = this.options.find((opt) => opt.value === this.value);
    return selectedOption ? selectedOption.label : this.placeholder;
  }

  // ControlValueAccessor methods
  onChange: (
    val: string | number | boolean | null | (string | number | boolean | null)[]
  ) => void = () => {
    // This empty implementation will be replaced by registerOnChange
    // We intentionally don't use the val parameter here
  };

  onTouched: () => void = () => {
    // This empty implementation will be replaced by registerOnTouched
  };

  writeValue(
    val: string | number | boolean | null | (string | number | boolean | null)[]
  ): void {
    this.value = val;
    this.onChange(this.value);
    this.cdr.detectChanges();
  }

  registerOnChange(
    fn: (
      val:
        | string
        | number
        | boolean
        | null
        | (string | number | boolean | null)[]
    ) => void
  ): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  toggleDropdown(): void {
    if (this.disabled) return;
    this.isOpen = !this.isOpen;
    this.openedChange.emit(this.isOpen);
    if (this.isOpen) {
      setTimeout(() => this.searchInput?.nativeElement.focus(), 0);
      this.focusedOptionIndex = -1;
    }
  }

  selectOption(option: SelectOption): void {
    if (this.disabled) return;
    if (this.multiple) {
      const current = Array.isArray(this.value) ? this.value : [];
      this.value = current.includes(option.value)
        ? current.filter((v) => v !== option.value)
        : [...current, option.value];
    } else {
      this.value = option.value;
      this.isOpen = false;
      this.openedChange.emit(false);
    }
    this.markTouched();
    this.onChange(this.value);
    this.selectionChange.emit(this.value);
  }

  isSelected(option: SelectOption): boolean {
    if (this.multiple && Array.isArray(this.value)) {
      return this.value.includes(option.value);
    }
    return this.value === option.value;
  }

  filterOptions(): void {
    this.filteredOptions = this.options.filter((opt) =>
      opt.label.toLowerCase().includes(this.searchText.toLowerCase())
    );
    this.focusedOptionIndex = -1;
  }

  clearSelection(event: Event): void {
    event.stopPropagation();
    this.value = this.multiple ? [] : null;
    this.searchText = '';
    this.filteredOptions = [...this.options];
    this.onChange(this.value);
    this.selectionChange.emit(this.value);
    this.markTouched();
  }

  markTouched(): void {
    this.touched = true;
    this.onTouched();
  }

  isInvalid(): boolean {
    return (
      this.required &&
      (this.value === null ||
        (Array.isArray(this.value) && this.value.length === 0)) &&
      this.touched
    );
  }

  @HostListener('document:click', ['$event'])
  handleClickOutside(event: MouseEvent): void {
    const target = event.target as HTMLElement;
    if (this.isOpen && !target.closest('lib-select')) {
      this.isOpen = false;
      this.openedChange.emit(this.isOpen);
    }
  }

  private scrollToFocusedOption(): void {
    if (this.focusedOptionIndex >= 0 && this.dropdown?.nativeElement) {
      const options = this.dropdown.nativeElement.querySelectorAll('li');
      if (options[this.focusedOptionIndex]) {
        options[this.focusedOptionIndex].scrollIntoView({
          behavior: 'smooth',
          block: 'nearest',
        });
      }
    }
  }

  @HostListener('keydown', ['$event'])
  handleKeyboard(event: KeyboardEvent): void {
    if (!this.isOpen) return;

    switch (event.key) {
      case 'ArrowDown':
        event.preventDefault();
        if (this.focusedOptionIndex < this.filteredOptions.length - 1) {
          this.focusedOptionIndex++;
        } else {
          this.focusedOptionIndex = 0; // Wrap around to start
        }
        this.scrollToFocusedOption();
        break;

      case 'ArrowUp':
        event.preventDefault();
        if (this.focusedOptionIndex > 0) {
          this.focusedOptionIndex--;
        } else {
          this.focusedOptionIndex = this.filteredOptions.length - 1; // Wrap around to end
        }
        this.scrollToFocusedOption();
        break;

      case 'Enter':
        event.preventDefault();
        if (
          this.focusedOptionIndex >= 0 &&
          this.focusedOptionIndex < this.filteredOptions.length
        ) {
          this.selectOption(this.filteredOptions[this.focusedOptionIndex]);
        }
        break;

      case 'Escape':
        event.preventDefault();
        this.isOpen = false;
        this.openedChange.emit(false);
        break;

      default:
        break;
    }
  }
}

import { Meta, StoryObj } from '@storybook/angular';
import { AlertComponent } from './alert.component';
import { applicationConfig } from '@storybook/angular';
import { provideAnimations } from '@angular/platform-browser/animations';

// Define the interface for the StackedAlerts story with flat properties
interface StackedAlertsArgs {
  firstMessage: string;
  firstType: 'success' | 'error' | 'warning' | 'info';
  firstAction: string | null;
  firstDismissible: boolean;
  firstIcon: string | null;
  firstDuration: number;
  firstAnimation: 'fade' | 'slide';
  firstPosition:
    | 'top-left'
    | 'top-center'
    | 'top-right'
    | 'bottom-left'
    | 'bottom-center'
    | 'bottom-right';

  secondMessage: string;
  secondType: 'success' | 'error' | 'warning' | 'info';
  secondAction: string | null;
  secondDismissible: boolean;
  secondIcon: string | null;
  secondDuration: number;
  secondAnimation: 'fade' | 'slide';
  secondPosition:
    | 'top-left'
    | 'top-center'
    | 'top-right'
    | 'bottom-left'
    | 'bottom-center'
    | 'bottom-right';

  thirdMessage: string;
  thirdType: 'success' | 'error' | 'warning' | 'info';
  thirdAction: string | null;
  thirdDismissible: boolean;
  thirdIcon: string | null;
  thirdDuration: number;
  thirdAnimation: 'fade' | 'slide';
  thirdPosition:
    | 'top-left'
    | 'top-center'
    | 'top-right'
    | 'bottom-left'
    | 'bottom-center'
    | 'bottom-right';
}

const meta: Meta<AlertComponent> = {
  title: 'Components/Alert',
  component: AlertComponent,
  decorators: [
    applicationConfig({
      providers: [provideAnimations()],
    }),
  ],
  args: {},
  argTypes: {
    message: { control: 'text' },
    type: {
      control: 'select',
      options: ['success', 'warning', 'error', 'info'],
    },
    duration: { control: 'number' },
    action: { control: 'text' },
    dismissible: { control: 'boolean' },
    position: {
      control: 'select',
      options: [
        'top-left',
        'top-center',
        'top-right',
        'bottom-left',
        'bottom-center',
        'bottom-right',
      ],
    },
    icon: {
      control: 'select',
      options: [
        '',
        'pizza',
        'mood',
        'star',
        'favorite',
        'thumb_up',
        'lightbulb',
        'coffee',
        'cloud',
        'music_note',
        'pets',
        'rocket',
        'beach_access',
      ],
    },
    animation: { control: 'select', options: ['fade', 'slide'] },
  },
};

export default meta;

type Story = StoryObj<AlertComponent>;

export const BasicSnackBar: Story = {
  args: {
    message: 'Disco party!',
    type: 'info',
    action: 'Dance',
    duration: 5000,
    position: 'bottom-center',
  },
};

export const ConfigurablePosition: Story = {
  args: {
    message: 'Pool party!',
    type: 'info',
    duration: 5000,
    position: 'bottom-right',
  },
};

export const DismissibleError: Story = {
  args: {
    message: 'Something went wrong',
    type: 'error',
    dismissible: true,
    duration: 0,
    position: 'top-right',
  },
};

export const SuccessWithAction: Story = {
  args: {
    message: 'Data saved successfully',
    type: 'success',
    action: 'Undo',
    duration: 3000,
    position: 'bottom-center',
  },
};

export const WarningWithCustomIcon: Story = {
  args: {
    message: 'Custom icon alert',
    type: 'warning',
    icon: 'star',
    dismissible: true,
    duration: 5000,
    position: 'top-center',
  },
};

export const StackedAlerts: StoryObj<AlertComponent & StackedAlertsArgs> = {
  render: (args: StackedAlertsArgs) => ({
    props: args,
    template: `
      <lib-alert
        [message]="firstMessage"
        [type]="firstType"
        [action]="firstAction"
        [dismissible]="firstDismissible"
        [icon]="firstIcon"
        [duration]="firstDuration"
        [animation]="firstAnimation"
        [position]="firstPosition"
        [bypassDuplicateCheck]="true"
      ></lib-alert>
      <lib-alert
        [message]="secondMessage"
        [type]="secondType"
        [action]="secondAction"
        [dismissible]="secondDismissible"
        [icon]="secondIcon"
        [duration]="secondDuration"
        [animation]="secondAnimation"
        [position]="secondPosition"
        [bypassDuplicateCheck]="true"
      ></lib-alert>
      <lib-alert
        [message]="thirdMessage"
        [type]="thirdType"
        [action]="thirdAction"
        [dismissible]="thirdDismissible"
        [icon]="thirdIcon"
        [duration]="thirdDuration"
        [animation]="thirdAnimation"
        [position]="thirdPosition"
        [bypassDuplicateCheck]="true"
      ></lib-alert>
    `,
  }),
  args: {
    firstMessage: 'First alert',
    firstType: 'success',
    firstAction: null,
    firstDismissible: true,
    firstIcon: 'star',
    firstDuration: 50000,
    firstAnimation: 'slide',
    firstPosition: 'top-right',

    secondMessage: 'Second alert',
    secondType: 'warning',
    secondAction: null,
    secondDismissible: true,
    secondIcon: null,
    secondDuration: 50000,
    secondAnimation: 'slide',
    secondPosition: 'top-right',

    thirdMessage: 'Third alert',
    thirdType: 'error',
    thirdAction: null,
    thirdDismissible: true,
    thirdIcon: null,
    thirdDuration: 50000,
    thirdAnimation: 'slide',
    thirdPosition: 'top-right',
  } as StackedAlertsArgs,
  argTypes: {
    // First Alert Controls
    firstMessage: {
      control: { type: 'text', disable: false },
      name: 'First Alert Message',
    },
    firstType: {
      control: 'select',
      options: ['success', 'error', 'warning', 'info'],
      name: 'First Alert Type',
    },
    firstAction: {
      control: { type: 'text', disable: false },
      name: 'First Alert Action',
    },
    firstDismissible: { control: 'boolean', name: 'First Alert Dismissible' },
    firstIcon: {
      control: 'select',
      options: [
        '',
        'pizza',
        'mood',
        'star',
        'favorite',
        'thumb_up',
        'lightbulb',
        'coffee',
        'cloud',
        'music_note',
        'pets',
        'rocket',
        'beach_access',
      ],
      name: 'First Alert Icon',
    },
    firstDuration: { control: 'number', name: 'First Alert Duration' },
    firstAnimation: {
      control: 'select',
      options: ['fade', 'slide'],
      name: 'First Alert Animation',
    },
    firstPosition: {
      control: 'select',
      options: [
        'top-left',
        'top-center',
        'top-right',
        'bottom-left',
        'bottom-center',
        'bottom-right',
      ],
      name: 'First Alert Position',
    },

    // Second Alert Controls
    secondMessage: {
      control: { type: 'text', disable: false },
      name: 'Second Alert Message',
    },
    secondType: {
      control: 'select',
      options: ['success', 'error', 'warning', 'info'],
      name: 'Second Alert Type',
    },
    secondAction: {
      control: { type: 'text', disable: false },
      name: 'Second Alert Action',
    },
    secondDismissible: { control: 'boolean', name: 'Second Alert Dismissible' },
    secondIcon: {
      control: 'select',
      options: [
        '',
        'pizza',
        'mood',
        'star',
        'favorite',
        'thumb_up',
        'lightbulb',
        'coffee',
        'cloud',
        'music_note',
        'pets',
        'rocket',
        'beach_access',
      ],
      name: 'Second Alert Icon',
    },
    secondDuration: { control: 'number', name: 'Second Alert Duration' },
    secondAnimation: {
      control: 'select',
      options: ['fade', 'slide'],
      name: 'Second Alert Animation',
    },
    secondPosition: {
      control: 'select',
      options: [
        'top-left',
        'top-center',
        'top-right',
        'bottom-left',
        'bottom-center',
        'bottom-right',
      ],
      name: 'Second Alert Position',
    },

    // Third Alert Controls
    thirdMessage: {
      control: { type: 'text', disable: false },
      name: 'Third Alert Message',
    },
    thirdType: {
      control: 'select',
      options: ['success', 'error', 'warning', 'info'],
      name: 'Third Alert Type',
    },
    thirdAction: {
      control: { type: 'text', disable: false },
      name: 'Third Alert Action',
    },
    thirdDismissible: { control: 'boolean', name: 'Third Alert Dismissible' },
    thirdIcon: {
      control: 'select',
      options: [
        '',
        'pizza',
        'mood',
        'star',
        'favorite',
        'thumb_up',
        'lightbulb',
        'coffee',
        'cloud',
        'music_note',
        'pets',
        'rocket',
        'beach_access',
      ],
      name: 'Third Alert Icon',
    },
    thirdDuration: { control: 'number', name: 'Third Alert Duration' },
    thirdAnimation: {
      control: 'select',
      options: ['fade', 'slide'],
      name: 'Third Alert Animation',
    },
    thirdPosition: {
      control: 'select',
      options: [
        'top-left',
        'top-center',
        'top-right',
        'bottom-left',
        'bottom-center',
        'bottom-right',
      ],
      name: 'Third Alert Position',
    },

    // Remove inherited fields that are not used in this story
    message: { table: { disable: true } },
    type: { table: { disable: true } },
    duration: { table: { disable: true } },
    action: { table: { disable: true } },
    dismissible: { table: { disable: true } },
    position: { table: { disable: true } },
    icon: { table: { disable: true } },
    animation: { table: { disable: true } },
  },
};

