import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  trigger,
  state,
  style,
  animate,
  transition,
} from '@angular/animations';

@Component({
  selector: 'lib-alert',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './alert.component.html',
  styleUrls: ['./alert.component.css'],
  animations: [
    trigger('fadeInOut', [
      state('void', style({ opacity: 0, transform: 'translateY(20px)' })),
      state('*', style({ opacity: 1, transform: 'translateY(0)' })),
      transition('void => *', animate('300ms ease-in')),
      transition('* => void', animate('300ms ease-out')),
    ]),
  ],
})
export class AlertComponent implements OnInit {
  @Input() message = '';
  @Input() type: 'success' | 'warning' | 'error' | 'info' = 'info';
  @Input() duration = 5000; // Renamed from autoDismiss to duration to match Material API
  @Input() action?: string;
  @Input() dismissible = true;
  @Input() position:
    | 'top-left'
    | 'top-center'
    | 'top-right'
    | 'bottom-left'
    | 'bottom-center'
    | 'bottom-right' = 'bottom-center';
  @Input() icon?: string; // Custom icon support (e.g., 'check', 'warning', 'error', 'info')

  @Output() actionClicked = new EventEmitter<void>(); // Emit event when action is clicked
  @Output() closed = new EventEmitter<void>(); // Emit event when alert is closed

  isVisible = true;

  ngOnInit() {
    if (this.duration > 0) {
      setTimeout(() => {
        this.close();
      }, this.duration);
    }
  }

  close() {
    this.isVisible = false;
    this.closed.emit();
  }

  onAction() {
    this.actionClicked.emit();
    this.close();
  }

  getIcon(): string {
    // Default icons based on type if no custom icon is provided
    if (this.icon) {
      return this.icon;
    }
    switch (this.type) {
      case 'success':
        return 'check_circle';
      case 'error':
        return 'error';
      case 'warning':
        return 'warning';
      case 'info':
      default:
        return 'info';
    }
  }
}

import { Component, Input, HostBinding } from '@angular/core';

@Component({
  selector: 'lib-button',
  standalone: true,
  templateUrl: './button.component.html',
  styles: [], // No inline styles; Tailwind handles it
})
export class ButtonComponent {
  @Input() variant: 'primary' | 'accent' | 'outline' | 'text' = 'primary';
  @Input() disabled = false;
  @Input() class = ''; // Allow users to pass custom Tailwind classes

  @HostBinding('class') get hostClasses() {
    const baseClasses =
      'inline-flex items-center justify-center px-4 py-2 rounded-md font-medium transition-colors duration-200';
    const variantClasses = {
      primary:
        'bg-blue-600 text-white hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2',
      accent:
        'bg-purple-600 text-white hover:bg-purple-700 focus:ring-2 focus:ring-purple-500 focus:ring-offset-2',
      outline:
        'border border-gray-300 text-gray-700 hover:bg-gray-100 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2',
      text: 'text-gray-700 hover:bg-gray-100 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2',
    };
    const disabledClasses = this.disabled
      ? 'opacity-50 cursor-not-allowed'
      : '';

    return `${baseClasses} ${variantClasses[this.variant]} ${disabledClasses} ${
      this.class
    }`.trim();
  }
}

import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'lib-icon',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './icon.component.html',
  styleUrls: ['./icon.component.css'],
})
export class IconComponent {
  @Input() name = 'home';
  @Input() size: 'sm' | 'md' | 'lg' | 'xl' = 'md';
  @Input() color = 'text-gray-500';

  get sizePx(): number {
    const sizeMap: Record<string, number> = {
      sm: 16,
      md: 24,  // Default Material Icon size
      lg: 32,
      xl: 48,
    };
    return sizeMap[this.size] || sizeMap['md'];
  }

  get colorClass(): string {
    return this.color;
  }
}

